import React, { useState, useEffect } from "react";
import axios from "axios";

function App() {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [message, setMessage] = useState("");
  const [feedbacks, setFeedbacks] = useState([]);

  const fetchFeedbacks = async () => {
    const res = await axios.get("http://127.0.0.1:8000/feedback");
    setFeedbacks(res.data);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    await axios.post("http://127.0.0.1:8000/feedback", {
      name,
      email,
      message
    });
    setName("");
    setEmail("");
    setMessage("");
    fetchFeedbacks();
  };

  useEffect(() => {
    fetchFeedbacks();
  }, []);

  return (
    <div style={{ padding: "20px" }}>
      <h2>Student Feedback Form</h2>
      <form onSubmit={handleSubmit}>
        <input value={name} onChange={e => setName(e.target.value)} placeholder="Name" /><br/>
        <input value={email} onChange={e => setEmail(e.target.value)} placeholder="Email" /><br/>
        <textarea value={message} onChange={e => setMessage(e.target.value)} placeholder="Message"></textarea><br/>
        <button type="submit">Submit</button>
      </form>

      <h3>All Feedbacks</h3>
      <ul>
        {feedbacks.map((f, i) => (
          <li key={i}>
            <b>{f.name}</b>: {f.message}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
